import { useEffect, useState } from 'react';
import './ImageMin.css'
function ImageMin(props) {

    const [liked, setLiked] = useState(false);
    const [deleted, setDeleted] = useState(false);

    useEffect(() => {
        console.log(props.liked)
        setLiked(props.liked)
    }, [])

    const anchorClick = (e) => {
        e.preventDefault();
    }

    const onLike = () => {
        // setLiked(!liked)
        fetch("http://localhost:3333"+"/like", {
            method: "POST",
            credentials: "include",
            headers: {
                "Content-Type" : "application/json",
                "Access-Control-Allow-Origin": "http://localhost:3333"
            },
            body: JSON.stringify({
                image: props.url
            })
        })
    }

    const onDlt = () => {
        setDeleted(true)
    }

    return (

        <div className="post-card">
            <div onClick={()=>anchorClick}>
                <h3>This is image title</h3>
                <img className = "post-card-img"src={"http://localhost:3333/image/"+props.url} />
                {
                    liked ? <button className='btn btn-success m-3' onClick={() => onLike()}>Liked</button> : <button className='btn btn-primary m-3' onClick={() => onLike()}>Like</button>
                }
                {
                    deleted ? <button className='btn btn-danger m-3'>Deleted</button> : <button className='btn btn-danger m-3' onClick={() => onDlt()}>Delete</button>
                }
                
                <h6>Source: Name</h6>
                <h6>Location: Area51</h6>
            </div>
        </div>
    )
}
export default ImageMin;
